import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'

export default class ArticleForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log("asfasf")
  }

  render() {
    return(
      <div>
        <Form id='find-article-form' onSubmit={this.handleSubmit}>
          <Form.Control className='article-input' type="email" placeholder="Enter a word" value={this.state.value} onChange={this.handleChange} />
          <Row className='flex-row-reverse'>
            <Col xs={12} md={5}>
              <Button className='margin-top-sm float-right' onClick={this.handleClick} type="submit">Find the article</Button>
            </Col>
          </Row>
        </Form>
      </div>
    )
  }
}
