import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import { useState } from 'react';

function ArticleForm() {
  const [input, setInput] = useState('');

  return(
    <div>
      <div className='word-text'><div className='article-text'>„Main“ </div>Artikel</div>
      <Form id='find-article-form'>
        <Form.Control className='article-input' type="email" placeholder="Enter a word" value={this.state.value} onChange={this.handleChange} />
        <Row className='flex-row-reverse'>
          <Col xs={12} md={5}>
            <Button className='margin-top-sm float-right' type="submit">Find the article</Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}

export default ArticleForm;
