import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import { useState } from 'react';

function ArticleForm() {
  const [word, setWord] = useState('Artikel');

  const handleSubmit = (e) => { 
    e.preventDefault();
    console.log("goo")
    setWord(e.target.word.value)
  }

  return(
    <div>
      <div className='word-text'><div className='article-text'>„Main“ </div>{word}</div>
      <Form id='find-article-form' onSubmit={handleSubmit}>
        <Form.Control className='article-input' name="word" placeholder="Enter a word"/>
        <Row className='flex-row-reverse'>
          <Col xs={12} md={5}>
            <Button className='margin-top-sm float-right' type="submit">Find the article</Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}

export default ArticleForm;
