import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import { useState } from 'react';

function ArticleForm() {
  const [word, setWord] = useState('Artikel');
  const [disabled, setDisabled] = useState(true);

  const words = [
    {
      "word": "Ei",
      "article": "die"
    }, 
    {
      "word": "Zucker",
      "article": "der"
    },
    {
      "word": "Kuchen",
      "article": "der"
    },
    {
      "word": "Mehl",
      "article": "das"
    }
  ]

  const handleSubmit = (e) => { 
    e.preventDefault();
    setWord(e.target.word.value)
  }

  // Disable the "find article" button, if the word input is empty.
  const handleWordInputChange = (e) => {
    setDisabled(e.target.value === "")
  }

  return(
    <div>
      <div className='word-text'><div className='article-text'>„Main“</div>{word}</div>
      <Form id='find-article-form' onSubmit={handleSubmit}>
        <Form.Control className='article-input' name="word" placeholder="Enter a word" onInput={(e) => handleWordInputChange(e)}/>
        <Row className='flex-row-reverse'>
          <Col xs={12} md={5}>
            <Button className={`margin-top-sm float-right ${disabled ? "disabled" : ""}`} type="submit">Find the article</Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}

export default ArticleForm;
