import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import { useEffect } from 'react';
import { useState } from 'react';
import axios from 'axios'

function ArticleForm() {
  const [article, setArticle] = useState('„Main“')
  const [word, setWord] = useState('Artikel');
  const [disabled, setDisabled] = useState(true);

  useEffect(() => {
    axios
      .get('http://localhost:9000/main-artikel/article')
      .then(response => {
        setArticle(response.data)
      })
  }, [])

  // Disable the "find article" button, if the word input is empty.
  const handleWordInputChange = (e) => {
    setDisabled(e.target.value === "")
  }

  const handleSubmit = (e) => { 
    e.preventDefault();
    setWord(e.target.word.value)
    console.log(article)
  }

  return(
    <div>
      <div className='word-text'><div className='article-text'>{article}</div>{word}</div>
      <Form id='find-article-form' onSubmit={handleSubmit}>
        <Form.Control className='article-input' name="word" placeholder="Enter a word" onInput={(e) => handleWordInputChange(e)}/>
        <Row className='flex-row-reverse'>
          <Col xs={12} md={5}>
            <Button className={`margin-top-sm float-right ${disabled ? "disabled" : ""}`} type="submit">Find the article</Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}

export default ArticleForm;
