import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import { useState } from 'react';
import axios from 'axios'

function ArticleForm() {
  const [article, setArticle] = useState('„Main“')
  const [word, setWord] = useState('Artikel');
  const [disableFind, setDisableFind] = useState(true);

  // Disable the "find article" button, if the word input is empty.
  const handleWordInputChange = (e) => {
   setDisableFind(e.target.value === "")
  }

  const getArticle = (word) => {
    axios
    .get('http://localhost:9000/main-artikel/article', { params: { word: word }})
    .then(response => {
      var article = response.data;
      if(article == "") {
        setArticle("NO")
      }
      else {
        setArticle(article)
      }

      setDisableFind(true)
    })
  }

  const handleSubmit = (e) => { 
    e.preventDefault();
    const word = e.target.word.value
    getArticle(word)
    setWord(word)
    console.log(article)
  }

  return(
    <div>
      <div className='word-text'><div className='article-text'>{article}</div>{word}</div>
      <Form id='find-article-form' onSubmit={handleSubmit}>
        <Form.Control className='article-input' name="word" placeholder="Enter a word" onInput={(e) => handleWordInputChange(e)}/>
        <Row className='flex-row-reverse'>
          <Col xs={12} md={5}>
            <Button className={`margin-top-sm float-right ${disableFind ? "disableFind" : ""}`} type="submit">Find the article</Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}

export default ArticleForm;
