import './App.css';
import Button from 'react-bootstrap/Button';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container';

function ArticleForm() {
  return(
    <div>
      <Form id='find-article-form' onSubmit={this.handleSubmit}>
        <Form.Control className='article-input' type="email" placeholder="Enter a word" />
        <Row className='flex-row-reverse'>
          <Col xs={12} md={5}>
          <Button className='margin-top-sm float-right'>Find the article</Button>
          </Col>
        </Row>
     </Form>
    </div>
  )
}

function App() {
  return (
    <div className="App">
      <Container className='game-container'>
        <Row>
          <Col xs={10} md={8} className='justify-content-center'>
            <Card>
              <Card.Body>
                <div className='article-text'><text>„Main“ </text>Artikel</div>
                <ArticleForm></ArticleForm>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
